INSERT INTO `tbl_noidungdiadiem` (`MaBaiViet`, `MaChuDe`, `MaNguoiDung`, `TieuDe`, `TomTat`, `NoiDung`, `NgayDang`, `HinhAnh`, `ChuThichAnh`, `LuotXem`, `KiemDuyet`) VALUES
(1, 1, 1, 'Giới thiệu du lịch ở Đà Nẵng', 'Đà Nẵng là thành phố lớn thứ 4 ở Việt Nam sau Thành phố Hồ Chí Minh, Hà Nội và Hải Phòng về đô thị hóa và phát triển kinh tế – xã hội. Nằm trên bờ Biển Đông có cửa sông Hàn, Đà Nẵng là một trong những thành phố cảng có vị trí chiến lược của miền Trung Việt Nam và là một trong 5 thành phố trực thuộc Trung ương.', 'Nằm ở vùng Nam Trung Bộ, Đà Nẵng có cả núi, đồng bằng và biển. Các điểm tham quan du lịch nổi tiếng khi du lịch Đà Nẵng bao gồm khu du lịch Bà Nà, bãi biển Mỹ Khê, và khu vui chơi giải trí trong nhà Fantasy Park lớn nhất châu Á. Đà Nẵng còn có nhiều thắng cảnh đẹp mê hồn như đèo Hải Vân, rừng nguyên sinh ở bán đảo Sơn Trà và Ngũ Hành Sơn. Đặc biệt, Đà Nẵng được bao quanh bởi 3 di sản văn hóa thế giới là Huế, Hội An và Mỹ Sơn, và xa hơn chút nữa là Vườn Quốc Gia Phong Nha – Kẻ Bàng. Hàng năm, Đà Nẵng tổ chức Lễ hội pháo hoa thu hút rất nhiều du khách muôn phương đến tham dự.\r\n\r\nBất cứ phương tiện nào cũng có thể đưa bạn đến được Đà Nẵng. Bên cạnh những lợi thế về cảnh quan, sự thuận tiện di chuyển cũng là một ưu thế của thành phố này. Bạn có thể đi máy bay, xe lửa, xe khách, xe máy,…\r\n\r\nĐà Nẵng là một thành phố thuộc vùng Nam Trung Bộ nên có khí hậu nhiệt đới gió mùa điển hình, nhiệt độ cao và ít biến động. Mỗi năm có hai mùa rõ rệt: mùa mưa từ tháng 8 đến tháng 12 và mùa khô từ tháng 1 đến tháng 7. Để tránh những ảnh hưởng của mưa bão thì thời điểm du lịch Đà Nẵng đẹp nhất là từ tháng 2 đến tháng 8 hằng năm.\r\n\r\nỞ Đà Nẵng có rất nơi bạn có thể tham quan như: Bảo tàng Nghệ thuật Điêu khắc Chăm Pa (thường gọi là Cổ viện Chàm), Cầu Sông Hàn, Cầu Rồng, Chợ Cồn, Chùa Linh Ứng, Bãi biển Mỹ Khê,... và rất nhiều địa điểm bạn có thể tham quan khi đến với Đà Nẵng.\r\n\r\n', '2023-05-11 15:05:20', 'images/danang1.jpg', '', 146, 1),
(2, 1, 3, 'Giới thiệu du lịch ở SaPa', 'Nằm dưới chân dãy Hoàng Liên Sơn hùng vĩ và chỉ cách Hà Nội 5 - 6 giờ di chuyển bằng đường cao tốc, Sa Pa là điểm đến lý tưởng quanh năm. Ngoài những thửa ruộng bậc thang vào mùa lúa chín, Sa Pa còn hút khách nhờ vẻ đẹp của nhiều điểm đến khác.', 'Sa Pa có khí hậu mát mẻ nên phù hợp với những chuyến du lịch quanh năm. Bạn chỉ cần tránh mùa mưa bão từ tháng 6 đến đầu tháng 8, bởi thời tiết cực đoan có thể gây lũ ống, lũ quét, sạt lở...\r\n\r\nTừ tháng 2 đến tháng 5, mùa xuân Sa Pa rực rỡ với trăm hoa khoe sắc như mai anh đào, đào, mận... Mùa hè từ tháng 6 đến tháng 8 là thời điểm khách du lịch đến Sa Pa để trốn nóng, ngắm những thửa ruộng bậc thang xanh mát.\r\n\r\nMùa thu kéo dài từ tháng 9 đến tháng 11, Sa Pa quyến rũ trong màu vàng óng ả của lúa chín và trời dần trở lạnh. Mùa đông đến từ tháng 12 đến tháng 2 năm sau, nếu may mắn bạn có thể \"săn\" tuyết, băng giá trên những đỉnh núi.\r\n\r\nMuốn đến SaPa, bạn có thể lựa chọn di chuyển bằng xe máy, tàu hỏa, máy bay, oto,...\r\n\r\nSa Pa có đủ loại hình lưu trú cho bạn chọn từ nhà nghỉ, homestay ở bản, hostel, khách sạn giá rẻ, cho tới các khách sạn 4-5 sao, resort cao cấp nằm biệt lập.\r\n\r\nBạn có thể dành khoảng 3 ngày 2 đêm để khám phá những điểm đến nổi tiếng tại Sa Pa.\r\n\r\nSaPa có những nơi để chúng ta tham quan như: Núi Hàm Rồng, Nhà thờ đá Sa Pa, Bản Cát Cát, Lao Chải - Tả Van, Đỉnh Fansipan, Swing Sa Pa, Hồ Séo Mý Tỷ, Cổng Trời, Đèo Ô Quy Hồ, Thác Bạc - Thác Tình Yêu,...\r\n\r\nĐến Sa Pa phải ăn đồ nướng, bởi đây là món ăn có mặt khắp nơi và nhiều loại từ các xiên thịt lợn, gà, rau củ cuốn cho tới trứng, ngô, khoai, cơm lam. Những quán ăn đặc sản du khách có thể tham khảo là thắng cố A Quỳnh (đường Thạch Sơn), các món lẩu cá ở Hotpot Center (đường Xuân Viên), phở cốn sủi ông Há (468 Điện Biên Phủ). Với những du khách trẻ, yêu thích món ăn và không gian trẻ trung nên đến với Mị nướng (đường Điện Biên Phủ) chuyên về các món nướng than hay Tiệm gà Túk Túk với sườn cay nướng tảng, gà lắc phô mai... và H\'Mong Pizza ở Pi\'s Boutique. Ở trung tâm thị xã cũng có rất nhiều quán cà phê để du khách lựa chọn. Tuy nhiên nếu bạn muốn tìm một không gian yên tĩnh, có view thung lũng hoặc núi rừng có thể đến với Coóng Cafe (cùng trong homestay Coóng ở đường đi bản Cát Cát), Vietrekking (đường Hoàng Liên), Gem Valley Cafe (bản Cát Cát).\r\n ', '2016-03-06 16:03:43', 'images/sapa.jpg', '', 43, 1),
(3, 1, 1, 'Tòa nhà cao nhất Việt Nam - Landmark 81', 'Địa chỉ: Số 720A Điện Biên Phủ, Quận Bình Thạnh, Thành phố Hồ Chí Minh\r\nGiờ mở cửa: 8:30 – 22:00\r\nGiá vé: 810.000 VNĐ/người lớn, 405.000 VNĐ/trẻ em từ 1m -1.4m, trẻ em dưới 1m miễn phí\r\nTòa nhà Landmark 81 là một trong những dự án lớn của Vingroup tại Thành phố Hồ Chí Minh. Công trình này có chiều cao lên tới 470m – hiện nay, đây là tòa nhà cao nhất tại Việt Nam. \r\n\r\n', '<p>Landmark 81 được xem l&agrave; một địa điểm du lịch S&agrave;i G&ograve;n l&yacute; tưởng. Tại đ&acirc;y, bạn c&oacute; thể thoải m&aacute;i vui chơi với đa dạng c&aacute;c dịch vụ tiện &iacute;ch, nhu cầu nghỉ dưỡng, vui chơi.&nbsp;Trong t&ograve;a nh&agrave; n&agrave;y c&oacute; trung t&acirc;m thương mại Vincom Center Landmark 81 gi&uacute;p cho bạn thoải m&aacute;i mua sắm với những thương hiệu h&agrave;ng đầu tr&ecirc;n thế giới.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, rất nhiều những dịch vụ vui chơi giải tr&iacute; mang đến cho bạn trải nghiệm thư gi&atilde;n tuyệt vời như: Rạp chiếu phim CGV, s&acirc;n băng Vincom Ice Rink&hellip; Tại địa điểm du lịch S&agrave;i G&ograve;n n&agrave;y, bạn c&ograve;n c&oacute; thể gh&eacute; qua 30 nh&agrave; h&agrave;ng với h&agrave;ng ngh&igrave;n m&oacute;n ăn hấp dẫn từ 5 ch&acirc;u lục.&nbsp;</p>\r\n\r\n<p><img alt=\"Vinpearl Landmark 81, Autograph Collection\" src=\"https://vn.vinpearllandmark81hotel.com/resourcefiles/home-content-section-image/hotel-entrance.jpg?version=3282023021008\" /></p>\r\n\r\n<p>T&ograve;a nh&agrave; Landmark 81 c&ograve;n được xem l&agrave; địa điểm nghỉ dưỡng tuyệt vời cho những chuyến c&ocirc;ng t&aacute;c tại S&agrave;i G&ograve;n. Kh&aacute;ch sạn&nbsp;<strong><a href=\"https://vinpearl.com/vi/hotels/vinpearl-luxury-landmark-81\">Vinpearl Luxury Landmark 81</a></strong>&nbsp;với hệ thống ph&ograve;ng nghỉ sang trọng, tiện nghi, view to&agrave;n cảnh s&ocirc;ng S&agrave;i G&ograve;n&hellip;hứa hẹn sẽ mang đến cho bạn trải nghiệm nghỉ dưỡng đẳng cấp tại th&agrave;nh phố n&agrave;y.&nbsp;</p>\r\n\r\n<p>Đến với địa điểm du lịch Hồ Ch&iacute; Minh nổi tiếng n&agrave;y, du kh&aacute;ch đừng qu&ecirc;n gh&eacute; 3 tầng lầu cao nhất để c&oacute; được những trải nghiệm dịch vụ tuyệt vời, ngắm nh&igrave;n to&agrave;n cảnh th&agrave;nh phố mang t&ecirc;n B&aacute;c từ độ cao bậc nhất.&nbsp;</p>\r\n', '2021-12-02 22:22:06', 'images/landmark.jpg', '', 19, 1),
(4, 1, 1, 'Giới thiệu du lịch ở Phú Quốc', 'Quần đảo Phú Quốc nằm trong vịnh Thái Lan, cách TP HCM khoảng 400 km về hướng tây. Nơi đây thu hút du khách trong và ngoài nước bởi các loại hình du lịch đa dạng, với tài nguyên biển, đảo phong phú; hệ sinh thái rừng, biển đa dạng.\r\n\r\nVùng biển Phú Quốc có 22 hòn đảo lớn, nhỏ, tổng diện tích khoảng 589,23 km2. Trong đó, đảo Phú Quốc lớn nhất được chia thành bắc đảo và nam đảo. Thị trấn Dương Đông nằm ở trung tâm.', 'Thời điểm lý tưởng nhất để du lịch Đảo Ngọc là từ khoảng tháng 11 đến tháng 4 năm sau. Đây là mùa khô ở phương Nam, trời ít mưa, biển lặng, sóng êm và nắng ấm thích hợp cho các hoạt động du lịch ngoài trời. Mùa này thích hợp cho những tour du lịch nghỉ dưỡng, không thích hợp cho khách đi bụi hoặc đi phượt.\r\n\r\nTừ khoảng tháng 5 đến tháng 10 là mùa mưa, đôi khi có bão nhưng Phú Quốc vẫn đông khách do rơi vào khoảng thời gian nghỉ hè. Nếu đi Phú Quốc mùa này, bạn nên đến vào khoảng cuối tháng 4, lúc này khách vẫn chưa đông và thời tiết còn đẹp, giá cả cũng không tăng quá cao như mùa cao điểm. Tháng 10 cũng là thời điểm giao mùa nên ít mưa.\r\n\r\nVietnam Airlines, Bamboo Airways và Vietjet Air cung cấp các chuyến bay thẳng đến Phú Quốc từ Hà Nội, Hải Phòng, Thanh Hóa, Vinh, Huế, Đà Nẵng, Nha Trang, Đà Lạt, Buôn Ma Thuột... Giá vé máy bay nếu may mắn bạn có thể săn được vé rẻ trên dưới một triệu đồng cho cả chiều đi lẫn về.\r\n\r\nSân bay của Phú Quốc cách trung tâm đảo khoảng 15 phút di chuyển. Một số khách sạn, resort cao cấp thường có dịch vụ đưa đón miễn phí từ sân bay. Bạn nên hỏi thêm về dịch vụ này khi đặt phòng ở Phú Quốc.\r\n\r\nNgoài các chuyến bay thẳng, du khách có nhiều lựa chọn thú vị hơn khi đi từ TP HCM đến Phú Quốc như xe máy, ôtô, xe khách, tàu cao tốc hoặc phà.\r\n\r\nDu khách có thể tìm thấy trên Phú Quốc những nhà nghỉ bình dân cho đến khu nghỉ dưỡng cao cấp mang đẳng cấp quốc tế. Nếu muốn tiết kiệm chi phí, bạn có thể ở homestay. Nếu đi cùng công ty, gia đình có trẻ nhỏ thì lựa chọn tốt nhất cho bạn là khách sạn ở thị trấn Dương Đông, tiện tới các điểm tham quan và mua sắm.\r\n\r\nKhông giống như Nha Trang với các bãi biển dễ ghé thăm từ đường lớn, đến Phú Quốc du khách thường phải đi xa để đến bãi biển. Vì vậy nếu muốn yên tĩnh, nghỉ dưỡng thì bạn nên ở resort, villa hoặc các bungalow gần bãi biển.\r\n\r\nCác điểm du lịch trên Phú Quốc được chia làm 4 khu chính là: Khu trung tâm ở thị trấn Dương Đông, Nam đảo, Bắc đảo và Đông đảo. Tuỳ lịch trình, bạn nên dành ít nhất hai ngày nếu muốn khám phá hết các điểm vui chơi trên đảo.\r\n\r\nBắc đảo Phú Quốc mang vẻ đẹp trù phú với rừng nguyên sinh. Ở đây cũng có những bãi biển, làng chài hoang sơ, chưa bị ảnh hưởng nhiều bởi thương mại. Dưới đây là một số điểm tham quan: Vườn quốc gia Phú Quốc, làng chài Rạch Vẹm, Vinpearl Safari, Mũi Gành Dầu\r\n\r\nĐến với Nam Đảo chúng ta có những nơi tham quan như: Bãi Sao, Bãi Trường, Dinh Bà và Dinh Cậu, Mũi Dinh Cậu, Nhà tù Phú Quốc, Chợ đêm Phú Quốc,...\r\n\r\nĐên với các hòn đảo ở Phú Quốc chúng ta có: Hòn Thơm, Hòn Dăm,...\r\n\r\nNếu là tín đồ của ấm thực, bạn nên đi Phú Quốc vào khoảng tháng 5 đến tháng 11. Đây là mùa đi biển của dân địa phương nên sẽ có nhiều hải sản tươi sống, giá thành cũng sẽ rẻ hơn như: Gỏi cá Trích, Ghẹ Hàm Ninh, Còi biên mai nướng,...\r\n\r\nLà vùng biển đảo, Phú Quốc nổi tiếng với các món hải sản tươi sống, nổi bật là ghẹ, nhum, cá trích, bào ngư, ốc gai. Những làng chài như Hàm Ninh, Cửa Cạn và Rạch Vẹm cũng là điểm đến gợi ý để thưởng thức hải sản tươi sống, giá rẻ theo phong cách địa phương.\r\n\r\nMột số đặc sản nổi tiếng Phú Quốc bạn có thể mua về làm quà là ngọc trai, nước mắm, hồ tiêu, rượu sim...\r\n\r\nVà có một số lưu ý khi đến với Phú Quốc: Không nên đi Phú Quốc vào mùa mưa bão.\r\n\r\nKhách sạn, nhà nghỉ mùa cao điểm thường cháy phòng, bạn nên đặt trước khoảng một tháng.\r\n\r\nMột số địa điểm trên Phú Quốc không cho du khách vào tham quan, bạn cần tuyệt đối tuân thủ, không đi vào khu vực cấm,...\r\n\r\n', '2016-03-06 16:05:23', 'images/phuquoc.jpg', '', 59, 1),
(5, 3, 2, 'Địa chỉ cắm trại đẹp ở Việt Nam - Núi Bà Đen – Tây Ninh', 'Là ngọn núi cao nhất miền Nam Việt Nam, núi Bà Đen được ví von là “Đệ nhất thiên sơn” với cảnh hữu tình, gắn liền với nhiều giai thoại ly kỳ.', '<p>N&uacute;i B&agrave; Đen nằm ph&iacute;a Đ&ocirc;ng Bắc của th&agrave;nh phố T&acirc;y Ninh, c&aacute;ch trung t&acirc;m 11km, thuộc địa phận x&atilde; Thạnh T&acirc;n. Khu vực n&uacute;i B&agrave; c&oacute; tổng diện t&iacute;ch khoảng 24km2 v&agrave; được cấu th&agrave;nh bởi 3 ngọn n&uacute;i nhỏ l&agrave; n&uacute;i Heo, n&uacute;i Phụng v&agrave; n&uacute;i B&agrave; Đen.</p>\r\n\r\n<p><img alt=\"Kinh nghiệm cắm trại Núi Bà Đen Tây Ninh dành cho mọi người –  Wordswithmeaning\" src=\"https://wordswithmeaning.org/wp-content/uploads/2021/08/cam-trai-nui-ba-den-1.jpg\" /></p>\r\n\r\n<p>Với độ cao 986m, cao nhất miền Nam, n&uacute;i B&agrave; Đen c&oacute; biệt danh &ldquo;N&oacute;c nh&agrave; Đ&ocirc;ng Nam Bộ&rdquo;. Nơi đ&acirc;y nổi tiếng với hệ thống ch&ugrave;a linh thi&ecirc;ng, thu h&uacute;t kh&aacute;ch thập phương đến chi&ecirc;m b&aacute;i như ch&ugrave;a Trung, ch&ugrave;a B&agrave; v&agrave; ch&ugrave;a Hang. Kh&ocirc;ng những thế, n&uacute;i B&agrave; Đen c&ograve;n sở hữu một hệ sinh th&aacute;i phong ph&uacute; v&agrave; cảnh quang tuyệt đẹp. Ch&iacute;nh v&igrave; thế m&agrave; từ l&acirc;u, nơi đ&acirc;y đ&atilde; được nhiều phượt thủ lựa chọn cho chuyến trekking leo n&uacute;i, chinh phục thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p><img alt=\"Khu du lịch núi Bà Đen - Điểm đến hùng vĩ đậm sắc màu tôn giáo\" src=\"https://duan-sungroup.com/wp-content/uploads/2022/10/hinh-leading-khu-du-lich-nui-ba-den-e1665814304438.jpg\" /></p>\r\n\r\n<p>B&ecirc;n cạnh việc thăm viếng, hiện nay, với sự đầu tư của Tập đo&agrave;n Sun Group &ndash; Sun World Baden Mountain đ&atilde; v&agrave; đang thu h&uacute;t kh&aacute;ch du lịch gần xa với việc x&acirc;y dựng hệ thống c&aacute;p treo l&ecirc;n đỉnh n&uacute;i. Theo đ&oacute; l&agrave; tượng phật bằng đồng cao nhất Ch&acirc;u &Aacute; &ndash; T&acirc;y Bổ Đ&agrave; Sơn, c&ugrave;ng với cảnh sắc thi&ecirc;n nhi&ecirc;n v&agrave; dịch vụ ăn uống k&egrave;m theo, n&uacute;i B&agrave; Đen ng&agrave;y c&agrave;ng khẳng định được vị thế v&agrave; chứng tỏ tiềm năng du lịch của tỉnh T&acirc;y Ninh n&oacute;i ri&ecirc;ng v&agrave; Việt Nam n&oacute;i chung. Hi vọng trong tương lai gần, dự &aacute;n của Sun World ho&agrave;n thiện để du kh&aacute;ch c&oacute; thể đến chi&ecirc;m b&aacute;i v&agrave; thăm th&uacute;, cảm nhận một T&acirc;y Ninh kh&ocirc;ng chỉ c&oacute; thế mạnh về n&ocirc;ng nghiệp, m&agrave; c&ograve;n l&agrave; chốn t&acirc;m linh huyền b&iacute; với cảnh đẹp thi&ecirc;n nhi&ecirc;n tuyệt vời. Ngo&agrave;i ra n&uacute;i B&agrave; Đen cũng l&agrave; một địa điểm cắm trại gần TPHCM tuyệt vời, cắm trại tr&ecirc;n đỉnh n&uacute;i B&agrave; Đen y&ecirc;u cầu phải đầy đủ t&uacute;i ngủ, chăn giữ nhiệt, lều cắm trại chống mưa do nhiệt buổi tối thường xuống thấp v&agrave; hay c&oacute; sương m&ugrave;, gi&oacute; to.</p>\r\n', '2016-03-06 16:06:24', 'images/tay-ninh11-9452.jpg', '', 12, 1),
(6, 3, 3, 'Địa chỉ cắm trại đẹp ở Việt Nam - Hồ Cốc Vũng Tàu', 'Khu du lịch Hồ Cốc nằm tại xã Hưng Biền, huyện Xuyên Mộc, tỉnh Bà Rịa – Vũng Tàu. Khi đến đây rồi, bạn sẽ bị mê hoặc bởi khung cảnh thiên nhiên hữu tình thơ mộng, đây là nơi rất thích hợp cho những bạn có đam mê check-in sống ảo. Hoặc những ai yêu thiên nhiên, sẽ là một trải nghiệm vô cùng ý nghĩa.', '<p><img alt=\"Bãi đá nét đẹp vô cùng độc đáo của bãi biển Hồ Cốc Bà Rịa - Vũng Tàu (Ảnh: Tuan Nguyen)\" src=\"https://cdn.vntrip.vn/cam-nang/wp-content/uploads/2017/08/bai-da-ho-coc.jpg\" /></p>\r\n\r\n<p>Hồ Cốc nằm tr&ecirc;n địa b&agrave;n x&atilde; Bưng Riềng, huyện Xuy&ecirc;n Mộc, tỉnh B&agrave; Rịa &ndash; Vũng T&agrave;u. Con đường v&agrave;o khu vực n&agrave;y c&oacute; kh&iacute; hậu v&ocirc; c&ugrave;ng tho&aacute;ng m&aacute;t nhờ được n&uacute;i rừng bao quanh. V&igrave; mới chỉ bắt đầu được đầu tư khai th&aacute;c n&ecirc;n cảnh sắc nơi đ&acirc;y gần như vẫn giữ được n&eacute;t hoang sơ vốn c&oacute; của n&oacute; với biển xanh, c&aacute;t trắng, h&agrave;ng c&acirc;y, b&atilde;i tắm rộng, đều l&agrave; những n&eacute;t đặc trưng thường thấy ở c&aacute;c b&atilde;i biển Vũng T&agrave;u.</p>\r\n\r\n<p>Người ta đến Hồ Cốc kh&ocirc;ng chỉ để tắm biển, m&agrave; c&ograve;n để lắng nghe. Nghe tiếng s&oacute;ng vỗ ngo&agrave;i khơi, nghe tiếng x&agrave;o xạc của h&agrave;ng dương mỗi khi c&oacute; ngọn gi&oacute; thổi qua, c&ugrave;ng tiếng chim đ&ocirc;i l&uacute;c lại r&acirc;m ran đ&acirc;u đ&oacute;. Bản nhạc tự nhi&ecirc;n m&agrave; h&agrave;i h&ograve;a, khiến cho t&acirc;m hồn người nghe nhẹ nh&agrave;ng m&agrave; thanh thản đến lạ k&igrave;.</p>\r\n\r\n<p><img alt=\"Khám phá bãi biển Hồ Cốc Vũng Tàu - Vntrip.vn\" src=\"https://cdn.vntrip.vn/cam-nang/wp-content/uploads/2017/08/ho-coc-vung-tau.jpg\" /></p>\r\n\r\n<p>Khu du lịch biển Hồ Cốc l&agrave; một trong những địa điểm cắm trại đẹp ở Việt Nam. C&aacute;c bạn c&oacute; thể c&ugrave;ng nhau dựng lều trại đơn giản tr&ecirc;n b&atilde;i đất trống v&agrave; rồi thỏa th&iacute;ch kh&aacute;m ph&aacute; c&aacute;c tr&ograve; chơi th&uacute; vị như l&aacute;i moto, lướt s&oacute;ng, hay thả d&aacute;ng chụp h&igrave;nh với view biển tuyệt đẹp. Nếu như c&aacute;c bạn chưa chuẩn bị lều sẵn, th&igrave; cũng đừng lo v&igrave; tại đ&acirc;y c&oacute; cho thu&ecirc; lều dựng với mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute; 70.000 đồng cho loại lều đ&ocirc;i. Sắp tới c&oacute; chuyến du lịch Vũng T&agrave;u th&igrave; h&atilde;y đến Hồ Cốc để trải nghiệm kh&ocirc;ng gian nghỉ dưỡng với biển c&ugrave;ng bạn b&egrave; dịp cuối tuần nh&eacute;.</p>\r\n', '2016-03-06 16:10:01', 'images/hococ.jpg', '', 10, 1),
(7, 2, 2, 'Thánh Địa Mỹ Sơn', 'Thánh địa Mỹ Sơn thuộc địa phận xã Duy Phú, huyện Duy Xuyên, tỉnh Quảng Nam, cách thành phố Đà Nẵng 70 km và Hội An 40 km. Đây là thánh địa Ấn Độ giáo của Vương quốc Chăm Pa thời xưa.', '<p><img src=\"https://i1-dulich.vnecdn.net/2014/12/27/Pic2-1419663842.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=o1k32oAJukEJiuGUEL-WFg\" /><br />\r\nTheo nghi lễ truyền thống, mỗi vị vua sau khi l&ecirc;n ng&ocirc;i đều đến Mỹ Sơn l&agrave;m lễ th&aacute;nh tẩy, d&acirc;ng c&uacute;ng lễ vật v&agrave; x&acirc;y dựng đền thờ.</p>\r\n\r\n<p>Khu vực n&agrave;y được ph&aacute;t hiện v&agrave;o năm 1885 bởi một to&aacute;n l&iacute;nh Ph&aacute;p. Mười năm sau (1895), nh&agrave; khảo cổ Camille Paris đ&atilde; tới đ&acirc;y ph&aacute;t quang v&agrave; t&igrave;m hiểu lần đầu ti&ecirc;n. Từ đ&oacute; đến năm 1904, rất nhiều nh&agrave; nghi&ecirc;n cứu v&agrave; khảo cổ kh&aacute;c cũng tới để v&eacute;n bức m&agrave;n b&iacute; mật ở đ&acirc;y như Louis Finot, Henri Permentier...&nbsp;</p>\r\n\r\n<p>Th&aacute;nh địa Mỹ Sơn nằm trong thung lũng c&oacute; đường k&iacute;nh khoảng 2 km, xung quanh l&agrave; đồi n&uacute;i. Nơi đ&acirc;y gồm 70 c&ocirc;ng tr&igrave;nh đền th&aacute;p được chia th&agrave;nh nhiều cụm v&agrave; x&acirc;y dựng theo c&ugrave;ng một nguy&ecirc;n tắc.</p>\r\n\r\n<p>Kết cấu mỗi cụm gồm đền thờ ch&iacute;nh, bao quanh l&agrave; những ng&ocirc;i th&aacute;p nhỏ hoặc c&ocirc;ng tr&igrave;nh phụ. Trong đ&oacute; đền ch&iacute;nh tượng trưng cho n&uacute;i Meru, trung t&acirc;m của vũ trụ, l&agrave; nơi hội tụ của thần linh v&agrave; thờ thần Siva.</p>\r\n\r\n<p>Th&aacute;p cổng nằm ph&iacute;a trước đền ch&iacute;nh, c&oacute; hai cửa th&ocirc;ng nhau ở hướng đ&ocirc;ng v&agrave; t&acirc;y. Mandapa l&agrave; ng&ocirc;i nh&agrave; d&agrave;i tiếp theo th&aacute;p cổng, d&ugrave;ng l&agrave;m nơi đ&oacute;n tiếp kh&aacute;ch h&agrave;nh hương, tiếp nhận lễ vật. Cạnh đền thờ ch&iacute;nh l&agrave; ng&ocirc;i th&aacute;p c&oacute; một hoặc hai ph&ograve;ng. Cửa ra v&agrave;o ở hướng bắc, d&ugrave;ng l&agrave;m nơi cất giữ c&aacute;c đồ tế lễ. Đền phụ thờ c&aacute;c vị thần tr&ocirc;ng coi hướng trời.</p>\r\n\r\n<p>Phong c&aacute;ch kiến tr&uacute;c ở đ&acirc;y được chia l&agrave;m 6 loại bao gồm: phong c&aacute;ch cổ, H&ograve;a Lai, Đồng Dương, Mỹ Sơn, Po Nagar v&agrave; phong c&aacute;ch B&igrave;nh Định.</p>\r\n\r\n<p>C&aacute;c họa tiết thường gặp l&agrave; hoa l&aacute;, động vật như voi hoặc sư tử, h&igrave;nh tượng Kala - Makara (một biểu tượng của người Chăm Pa), hoạt cảnh vũ nữ Apsara, nhạc c&ocirc;ng, chư thi&ecirc;n đứng hộ tr&igrave; hay thủy qu&aacute;i Makara...</p>\r\n\r\n<p>Khoảng thời gian từ 1965 đến năm 1972, khu vực Duy Xuy&ecirc;n trở th&agrave;nh một chiến trường. C&ugrave;ng c&aacute;c x&oacute;m l&agrave;ng, th&aacute;nh địa n&agrave;y hứng chịu những tổn thất nặng nề. Đặc biệt, trận bom năm 1969 đ&atilde; l&agrave;m biến dạng h&igrave;nh h&agrave;i, khiến hầu hết c&aacute;c t&ograve;a đền th&aacute;p bị sụp đổ hoặc hư hại lớn.</p>\r\n\r\n<p>Năm 1980, trong khu&ocirc;n khổ chương tr&igrave;nh hợp t&aacute;c văn h&oacute;a Việt Nam &ndash; Ba Lan, tiểu ban phục hồi di t&iacute;ch Chăm Pa được th&agrave;nh lập do cố kiến tr&uacute;c sư Kazimiers Kwiatkowske phụ tr&aacute;ch. Khi ấy, di t&iacute;ch n&agrave;y được dọn dẹp, gia cố v&agrave; kh&ocirc;i phục. Nhờ đ&oacute;, c&aacute;c khu vực giữ lại được d&aacute;ng vẻ như ng&agrave;y h&ocirc;m nay.</p>\r\n', '2023-05-17 11:11:06', 'images/myson.png', '', 7, 1),
(8, 4, 1, 'King Kong sẽ có những cảnh quay trên không ở vịnh Hạ Long', ' Sáng 16/3, đoàn làm phim \"King Kong\" sẽ thực hiện những cảnh quay trên không ở vịnh Hạ Long. ', '<p>\r\n	Ph&oacute;ng vi&ecirc;n <em>Zing.vn đ&atilde;</em> c&oacute; cuộc trao đổi nhanh với &ocirc;ng H&agrave; Quang Long, Gi&aacute;m đốc Sở Văn h&oacute;a Thể thao Du lịch tỉnh Quảng Ninh, khi đo&agrave;n l&agrave;m phim <em>King Kong</em> chuẩn bị đến đ&acirc;y.</p>\r\n<p>\r\n	<em>&nbsp;</em><em>- Để chuẩn bị cho những ng&agrave;y quay sắp tới, ph&iacute;a tỉnh Quảng Ninh đ&atilde; l&agrave;m việc như thế n&agrave;o với đo&agrave;n l&agrave;m phim King Kong, thưa &ocirc;ng?</em></p>\r\n<p>\r\n	- Ph&iacute;a Sở Văn h&oacute;a Thể thao Du lịch ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; những buổi l&agrave;m việc cụ thể với đại diện đo&agrave;n l&agrave;m phim <em>King Kong</em>. Theo đ&oacute;, tỉnh Quảng Ninh sẽ hỗ trợ ở mức cao nhất để đảm bảo sự an to&agrave;n tuyệt đối cho đo&agrave;n l&agrave;m phim.</p>\r\n<p>\r\n	Ng&agrave;y 15/3, to&agrave;n bộ thiết bị m&aacute;y quay của l&agrave;m phim c&ugrave;ng với 20 xe &ocirc; t&ocirc; chuy&ecirc;n dụng v&agrave; trực thăng sẽ tập kết tại khu du lịch Tuần Ch&acirc;u. Ng&agrave;y 16/3, trực thăng sẽ thực hiện một số cảnh tr&ecirc;n kh&ocirc;ng ở vịnh Hạ Long. Chiều c&ugrave;ng ng&agrave;y, đạo diễn, diễn vi&ecirc;n sẽ thực hiện chuyến khảo s&aacute;t thực địa cuối c&ugrave;ng để quyết định bối cảnh quay <em>King Kong</em> những ng&agrave;y sau đ&oacute;.</p>\r\n<p>\r\n	Tất cả đo&agrave;n l&agrave;m phim gồm 200 nh&acirc;n sự sẽ lưu tr&uacute; tại khu du lịch Tuần Ch&acirc;u v&agrave; một số kh&aacute;ch sạn ở Hạ Long.</p>\r\n<table align=\"left\" class=\"picture\">\r\n	<tbody>\r\n		<tr>\r\n			<td class=\"pic\">\r\n				<img alt=\"‘King Kong sẽ có những cảnh quay trên không ở vịnh Hạ Long’\" src=\"http://img.v3.news.zdn.vn/w660/Uploaded/pcwvovbb/2016_03_12/onglong.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"pCaption caption\">\r\n				&Ocirc;ng H&agrave; Quang Long - Gi&aacute;m đốc Sở Văn h&oacute;a Thể thao Du lịch tỉnh Quảng Ninh. <em>Ảnh: <strong>B&aacute;o điện tử Quảng Ninh</strong></em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<em>- Ph&iacute;a tỉnh Quảng Ninh đ&atilde; chuẩn bị như thế n&agrave;o để hỗ trợ đo&agrave;n l&agrave;m phim những ng&agrave;y sắp tới?</em></p>\r\n<p>\r\n	- Ch&uacute;ng t&ocirc;i đ&atilde; l&ecirc;n kế hoạch cụ thể về việc đưa đ&oacute;n đo&agrave;n l&agrave;m phim, về việc chuẩn bị nơi ăn chốn ở, về việc sẽ hỗ trợ họ trong c&ocirc;ng t&aacute;c thực địa, v&agrave; đặc biệt l&agrave; đảm bảo an to&agrave;n, an ninh ở những nơi đo&agrave;n l&agrave;m phim sẽ quay. Tỉnh đang cố gắng lo chu tất mọi việc ở mức cao nhất.</p>\r\n<p>\r\n	Ch&uacute;ng t&ocirc;i x&aacute;c định việc hợp t&aacute;c với đo&agrave;n l&agrave;m phim <em>King Kong</em>, đ&ocirc;i b&ecirc;n sẽ c&ugrave;ng c&oacute; lợi. Vịnh Hạ Long xưa nay đ&atilde; nổi tiếng l&agrave; điểm đến hấp dẫn với kh&aacute;ch du lịch quốc tế. Vịnh Hạ Long được UNESCO c&ocirc;ng nhận l&agrave; Di sản Thi&ecirc;n nhi&ecirc;n Thế giới. Việc quay phim tại vịnh Hạ Long c&oacute; những t&aacute;c động t&iacute;ch cực tới đo&agrave;n l&agrave;m phim <em>King Kong</em> v&agrave; ngược lại, việc đo&agrave;n l&agrave;m phim đến đ&acirc;y cũng sẽ tăng sức hấp dẫn cho vịnh Hạ Long.</p>\r\n<p>\r\n	<em>- Ngo&agrave;i vịnh Hạ Long, đo&agrave;n l&agrave;m phim King Kong sẽ c&ograve;n quay ở l&agrave;ng ch&agrave;i Cửa Vạn. &nbsp;L&agrave;ng ch&agrave;i Cửa Vạn đ&atilde; được website du lịch Journeyetc đưa v&agrave;o danh s&aacute;ch 16 ng&ocirc;i l&agrave;ng cổ đẹp nhất thế giới (năm 2012). Theo đ&aacute;nh gi&aacute; của &ocirc;ng, Quảng Ninh đang đứng trước những cơ hội lớn để quảng b&aacute; du lịch như thế n&agrave;o?</em></p>\r\n<p>\r\n	- Theo kế hoạch, l&agrave;ng ch&agrave;i Cửa Vạn chỉ đang nằm trong dự kiến của đo&agrave;n l&agrave;m phim. Như t&ocirc;i đ&atilde; chia sẻ, tất cả c&ograve;n chưa được quyết định cụ thể. Chiều 16/3, đạo diễn phim sẽ thực hiện chuyến khảo s&aacute;t thực địa cuối c&ugrave;ng, sau đ&oacute; họ mới quyết định ch&iacute;nh thức sẽ quay tại những địa điểm n&agrave;o ở Quảng Ninh, ngo&agrave;i vịnh Hạ Long.</p>\r\n<p>\r\n	<em>Ngo&agrave;i việc đảm bảo an to&agrave;n, thắt chặt an ninh ở c&aacute;c điểm quay phim, ph&iacute;a đo&agrave;n l&agrave;m phim c&ograve;n đưa ra những y&ecirc;u cầu g&igrave; với tỉnh?</em></p>\r\n<p>\r\n	- Ch&uacute;ng t&ocirc;i sẽ bảo vệ phim trường, giữ an ninh, hỗ trợ họ t&agrave;u cao tốc để di chuyển&hellip; V&agrave; nhiều hạng mục kh&aacute;c.</p>\r\n<p>\r\n	- <em>V&agrave; kế hoạch của tỉnh, của Sở Văn h&oacute;a Thể thao Du lịch với những nơi đo&agrave;n l&agrave;m phim đi qua, liệu đ&oacute; c&oacute; phải l&agrave; những điểm du lịch &lsquo;n&oacute;ng&rsquo; của tỉnh trong tương lai?</em></p>\r\n<p>\r\n	- Đ&oacute; l&agrave; những bước tiếp theo m&agrave; tỉnh đ&atilde; t&iacute;nh đến. Tuy nhi&ecirc;n, hiện tại, t&ocirc;i cho rằng, h&atilde;y tập trung hỗ trợ để đo&agrave;n l&agrave;m phim quay tốt nhất, hiệu quả nhất đ&atilde;.</p>\r\n', '2016-03-13 09:07:40', 'images/halongthumb.jpg', 'Phim King Kong sẽ thực hiện những cảnh quay flycam trên vịnh Hạ Long ngày 16/3 tới. Tối nay, 12/3, đoàn làm phim sẽ liên hoan chia tay ở Ninh Bình, kết thúc những ngày quay ở đây', 20, 1),
(9, 2, 3, 'Lễ cúng bến nước độc đáo ở Tây Nguyên', 'Người Tây Nguyên tất bật sắm sanh lễ vật để cúng bến nước và thần nước, vị thần linh thiêng bậc nhất miền đất này, như một cách chống lại tình trạng nắng hạn gay gắt đang diễn ra. ', '<p>\r\n	<strong>Th&ocirc;n d&atilde; m&agrave; trữ t&igrave;nh</strong></p>\r\n<p>\r\n	Trở lại c&aacute;c bu&ocirc;n l&agrave;ng T&acirc;y Nguy&ecirc;n lần n&agrave;y, người bạn đam m&ecirc; t&igrave;m hiểu về d&acirc;n tộc học V&otilde; Trần Quốc say sưa chụp ảnh, ngay cả những cảnh sinh hoạt đời thường. N&agrave;y đ&acirc;y những thửa ruộng nứt nẻ chờ nước, những vạt rừng kh&ocirc; khốc chỉ cần một mồi lửa l&agrave; b&ugrave;ng l&ecirc;n th&agrave;nh đ&aacute;m ch&aacute;y lớn. C&ograve;n kia l&agrave; nhịp ch&agrave;y đ&ocirc;i gi&atilde; gạo của những ch&agrave;ng trai c&ocirc; g&aacute;i với l&agrave;n da n&acirc;u chắc lẳn, đ&ocirc;i mắt đẹp đa cảm; những bước ch&acirc;n nh&uacute;n nhảy v&agrave; m&aacute;i t&oacute;c chảy tr&agrave;n như suối của c&aacute;c sơn nữ tr&ecirc;n đường tắm suối trở về.</p>\r\n<p>\r\n	Anh Quốc cho ch&uacute;ng t&ocirc;i xem những tranh ảnh về sơn nữ ngực trần của c&aacute;c họa sĩ Đinh Cường, L&ecirc; Ho&agrave;ng, Phan Dũng&hellip; m&agrave; anh sưu tập được. Tranh rất đẹp như m&ocirc; tả trong sử thi T&acirc;y Nguy&ecirc;n, c&aacute;c sơn nữ quấn v&aacute;y v&agrave; để ngực trần với bờ vai tr&ograve;n, eo thon, ngực săn nh&ocirc; l&ecirc;n như cặp ng&agrave; non cong v&uacute;t. Anh h&agrave;o hứng kể chừng 30 năm trước từng bắt gặp c&aacute;c sơn nữ ngực trần tắm suối hay cảnh c&aacute;c c&ocirc; g&ugrave;i những quả bầu đựng nước thoăn thoắt leo l&ecirc;n c&aacute;c bậc thang l&agrave;m nh&uacute;n nhẩy bầu ngực tr&ograve;n mọng. Chỉ tiếc l&uacute;c ấy kh&ocirc;ng c&oacute; m&aacute;y ảnh để lưu lại những khoảnh khắc ấn tượng.</p>\r\n<p>\r\n	&ldquo;Đ&uacute;ng l&agrave; những năm đ&oacute; sơn nữ vẫn c&ograve;n để ngực trần. Tốt khoe, xấu che m&agrave;! V&ugrave;ng n&agrave;y m&ugrave;a kh&ocirc; n&oacute;ng bức lắm n&ecirc;n cởi trần m&aacute;t mẻ, thoải m&aacute;i hơn&rdquo;, gi&agrave; l&agrave;ng Điểu K&rsquo;Mốt (người S&rsquo;ti&ecirc;ng, th&ocirc;n 3, x&atilde; Gia Viễn, C&aacute;t Ti&ecirc;n, L&acirc;m Đồng) x&aacute;c nhận. C&ograve;n theo c&aacute;c nh&agrave; d&acirc;n tộc học, c&aacute;c tộc người T&acirc;y Nguy&ecirc;n đa phần theo chế độ mẫu hệ, tục để ngực trần c&oacute; thể xuất ph&aacute;t từ &yacute; niệm về phồn thực, sự sinh s&ocirc;i, nảy nở sung t&uacute;c, tốt tươi của đồng b&agrave;o thiểu số.</p>\r\n<p>\r\n	&ldquo;Đ&ocirc;i ch&acirc;n trần từ l&uacute;c sinh th&agrave;nh đ&atilde; được ng&acirc;m trong d&ograve;ng nước m&aacute;t s&ocirc;ng Mẹ v&agrave; sưởi ấm từ ngọn lửa rừng Cha n&ecirc;n mạnh như hổ, nhanh như s&oacute;c chinh phục bao ngọn n&uacute;i cao, vượt bao con s&ocirc;ng d&agrave;i để lập l&agrave;ng sinh sống. Người T&acirc;y Nguy&ecirc;n quan niệm mọi thứ đều c&oacute; linh hồn; nước, lửa, rừng, n&uacute;i&hellip; được cai quản bởi c&aacute;c vị thần, do đ&oacute; với bất cứ việc trọng đại n&agrave;o trong đời mỗi con người v&agrave; cộng đồng đều phải l&agrave;m lễ c&uacute;ng để xin ph&eacute;p thần linh&rdquo;, gi&agrave; l&agrave;ng Krajan Plin (bu&ocirc;n Đang Ja, huyện Lạc Dương, L&acirc;m Đồng) n&oacute;i.</p>\r\n<p>\r\n	<strong>Lễ c&uacute;ng c&oacute; từ l&acirc;u đời</strong></p>\r\n<p>\r\n	Trong căn nh&agrave; truyền thống d&agrave;i hun h&uacute;t của nguời Mạ ở bu&ocirc;n Hang Kar (v&ugrave;ng s&acirc;u v&ugrave;ng xa huyện Bảo L&acirc;m, tỉnh L&acirc;m Đồng), gi&agrave; l&agrave;ng K&rsquo;Pieu vừa khơi cho ngọn lửa bếp ở giữa nh&agrave; s&agrave;n b&ugrave;ng l&ecirc;n để c&oacute; th&ecirc;m hơi ấm vừa kể qu&aacute; tr&igrave;nh t&igrave;m đất lập l&agrave;ng, việc đầu ti&ecirc;n l&agrave; t&igrave;m nguồn nước để đ&aacute;p ứng nhu cầu sinh hoạt v&agrave; sản xuất. Đến khi ph&aacute;t hiện ra nguồn nước, người T&acirc;y Nguy&ecirc;n phải c&uacute;ng thần để xin ph&eacute;p sử dụng. Kể cả khi l&agrave;ng đ&atilde; định cư l&acirc;u đời th&igrave; cứ đến th&aacute;ng 3 h&agrave;ng năm (khi l&uacute;a, bắp, c&agrave; ph&ecirc; vừa được thu hoạch xong, chất đầy kho) lại tổ chức lễ c&uacute;ng bến nước để tỏ l&ograve;ng biết ơn với những vị thần thi&ecirc;ng li&ecirc;ng đ&atilde; che chở v&agrave; gi&uacute;p đỡ cho bu&ocirc;n l&agrave;ng.</p>\r\n', '2016-03-13 14:46:33', 'images/11.jpg', 'Tây nguyên', 3, 1),
(10, 4, 1, 'Kinh nghiệm du lịch Đà Lạt tự túc 2021 hơn cả hướng dẫn viên', 'Đà Lạt, trong mình là một thành phố đẹp, đẹp ở mọi thứ. Đà Lạt, dù đến rồi vẫn không thấy đủ, vùng đất này bao lần vẫn níu chân mình ở lại. Sau 4 lần đến và ở lại Đà Lạt, mình nghĩ rằng đã có đủ kinh nghiệm du lịch Đà Lạt tự túc để cùng chia sẻ lại với mọi người. Qua những gì mình đã trải nghiệm, tìm hiểu trong bài viết này sẽ hướng dẫn giúp mọi người tự lên lịch trình đi tham quan các địa điểm du lịch nổi tiếng hợp lý nhất. Chọn được những khách sạn ưng ý, địa chỉ MUA SẮM và ĂN UỐNG với giá SIÊU RẺ.\r\n', '<p>Để m&igrave;nh n&oacute;i sơ cho bạn biết Đ&agrave; Lạt c&oacute; g&igrave; nh&eacute;! L&agrave; ngọn n&uacute;i Langbiang huyền thoại, thu h&uacute;t nhiều bạn phượt trẻ tuổi, l&agrave; hồ Tuyền L&acirc;m thơ mộng l&agrave;m bao t&acirc;m hồn thổn thức, l&agrave; thung lũng t&igrave;nh y&ecirc;u điểm hẹn của đ&ocirc;i bạn trẻ, th&aacute;c Voi &ndash; con th&aacute;c huyền b&iacute; giữa n&uacute;i rừng. V&agrave; cả một &ldquo;gia t&agrave;i&rdquo; về những điểm đến, m&agrave; m&igrave;nh đ&atilde; chắt chiu qua những chuyến đi, v&agrave; c&oacute; cả t&igrave;m hiểu. Đến Đ&agrave; Lạt, kh&ocirc;ng cần phải vội, chỉ cần dạo bước tr&ecirc;n những con đường, cũng đủ thấy y&ecirc;n b&igrave;nh, chỉ muốn ở lại m&atilde;i. C&oacute; lẽ v&igrave; thi&ecirc;n nhi&ecirc;n Đ&agrave; Lạt qu&aacute; đẹp, m&agrave; người ta đ&atilde; đặt cho th&agrave;nh phố n&agrave;y biết bao nhi&ecirc;u l&agrave; c&aacute;i t&ecirc;n mỹ miều. L&agrave; th&agrave;nh phố sương m&ugrave; cũng đ&uacute;ng, m&agrave; th&agrave;nh phố ng&agrave;n hoa cũng chẳng sai, rồi th&agrave;nh phố đ&aacute;ng sống nhất Việt Nam. V&agrave; cứ mỗi lần nhắc đến Đ&agrave; Lạt, l&agrave; chỉ thấy những c&aacute;i hay, c&aacute;i đẹp, l&agrave; nơi nghỉ dưỡng l&yacute; tưởng m&agrave; điểm phượt cũng hợp l&yacute; nữa. DU LỊCH Đ&Agrave; LẠT BẠN N&Ecirc;N ĐI THỜI ĐIỂM N&Agrave;O L&Yacute; TƯỞNG? Với Kinh nghiệm du lịch Đ&agrave; Lạt th&igrave; theo m&igrave;nh cảm nhận Đ&agrave; Lạt kh&ocirc;ng giống như c&aacute;c th&agrave;nh phố kh&aacute;c khi du lịch chỉ mang t&iacute;nh thời vụ. Ri&ecirc;ng Đ&agrave; Lạt, bạn c&oacute; thể đến quanh năm bởi mỗi m&ugrave;a lại c&oacute; n&eacute;t đẹp ri&ecirc;ng của n&oacute;, thời tiết &ocirc;n h&ograve;a &iacute;t nắng gi&oacute; hay mưa b&atilde;o. Bạn c&oacute; thể thoải m&aacute;i lựa chọn thời điểm ph&ugrave; hợp nhất cho chuyến đi Đ&agrave; Lạt của m&igrave;nh. Nguồn b&agrave;i viết: https://dulichkhampha24.com/kinh-nghiem-du-lich-da-lat.html</p>\r\n', '2021-12-02 21:58:55', 'images/da-lat-.jpg', '', 11, 1),
(11, 4, 1, '4 ngày du ngoạn Đăk Lăk dịp hội đua voi', 'Trong 3 ngày hội, các hoạt động liên quan đến voi diễn ra trong suốt ngày 13/3. Bạn có thể lên lịch trình tham quan các điểm đến khác trong 2 ngày còn lại. ', '<p>\r\n	<strong>Ng&agrave;y 12/3</strong></p>\r\n<p>\r\n	5h: Đến Đăk Lăk, nhận ph&ograve;ng ở kh&aacute;ch sạn. Nghỉ ngơi, ăn s&aacute;ng.</p>\r\n<p>\r\n	8h: Xuất ph&aacute;t đi l&agrave;ng c&agrave; ph&ecirc; Trung Nguy&ecirc;n, nơi bạn c&oacute; thể ph&aacute;m ph&aacute;, t&igrave;m hiểu về c&aacute;c loại c&agrave; ph&ecirc;, lịch sử ph&aacute;t triển, thưởng thức c&agrave; ph&ecirc;.</p>\r\n<p>\r\n	9h: Tham quan đ&igrave;nh Lạc Giao, nơi thờ cụ Đ&agrave;o Duy Từ, một di t&iacute;ch lịch sử c&oacute; bề d&agrave;y lịch sử v&agrave; kiến tr&uacute;c đẹp.</p>\r\n<p>\r\n	10h: Tham quan Sắc tứ Khải Đoan tự, ng&ocirc;i ch&ugrave;a do Đoan Huy ho&agrave;ng th&aacute;i hậu cho x&acirc;y dựng v&agrave; Nam Phương ho&agrave;ng hậu trực tiếp chịu tr&aacute;ch nhiệm quản l&yacute; thi c&ocirc;ng.</p>\r\n<p>\r\n	12h: Gh&eacute; bu&ocirc;n AK&ocirc; Đh&ocirc;ng, ng&ocirc;i l&agrave;ng &Ecirc; Đ&ecirc; l&acirc;u đời nhất Bu&ocirc;n Ma Thuột.</p>\r\n<p>\r\n	13h: Thưởng thức đặc sản của Đăk Lăk.</p>\r\n<p>\r\n	14h: Đến nh&agrave; đ&agrave;y Bu&ocirc;n Ma Thuột, một trong những nh&agrave; t&ugrave; t&agrave;n bạo nhất của ch&iacute;nh quyền thực d&acirc;n Ph&aacute;p.</p>\r\n<p>\r\n	15h: Thăm t&ograve;a gi&aacute;m mục Ban M&ecirc; Thuột, c&ocirc;ng tr&igrave;nh c&oacute; kiến tr&uacute;c c&ocirc;ng phu v&agrave; mang đậm dấu ấn phong c&aacute;ch nh&agrave; d&agrave;i truyền thống của người &Ecirc; Đ&ecirc; bản địa.</p>\r\n<p>\r\n	16h: Thăm khu du lịch Hồ - Đồi Th&ocirc;ng Cư Dlu&ecirc; hay khu du lịch hồ Ea Kao.</p>\r\n<p>\r\n	19h: Thưởng thức buổi tối. Kh&aacute;m ph&aacute; TP Bu&ocirc;n M&ecirc; Thuột về đ&ecirc;m.</p>\r\n<p>\r\n	7h: Xuất ph&aacute;t đi bu&ocirc;n Đ&ocirc;n</p>\r\n<p>\r\n	8h-16h: Tham gia c&aacute;c hoạt động li&ecirc;n quan đến voi trong ng&agrave;y n&agrave;y gồm thi voi đ&aacute; b&oacute;ng, thi voi chạy, thi voi bơi.</p>\r\n<p>\r\n	15-17h: Tham gia trong hoạt động du kh&aacute;ch cưỡi voi tham quan thắng cảnh bu&ocirc;n Đ&ocirc;n.</p>\r\n<p>\r\n	16-16h30: Lễ tắm v&agrave; c&uacute;ng sức khỏe cho voi.</p>\r\n<p>\r\n	17h: Thưởng thức c&aacute;c m&oacute;n ăn trong lễ hội v&agrave; tham gia hội trại</p>\r\n<p>\r\n	20h: Về kh&aacute;ch sạn nghỉ ngơi</p>\r\n<p>\r\n	<strong>Ng&agrave;y 14/3</strong></p>\r\n<p>\r\n	8h: Xuất ph&aacute;t đi hồ Lăk. C&aacute;c trải nghiệm n&ecirc;n c&oacute; tại đ&acirc;y l&agrave; l&ecirc;nh đ&ecirc;nh tr&ecirc;n thuyền ra giữa hồ, cưỡi voi kh&aacute;m ph&aacute; hồ Lak, chi&ecirc;m ngưỡng những d&atilde;y n&uacute;i lớn được bao phủ bởi c&aacute;c c&aacute;nh rừng nguy&ecirc;n sinh.</p>\r\n<p>\r\n	11h: Chi&ecirc;m ngưỡng những kiến tr&uacute;c lịch sử như biệt điện của ho&agrave;ng đế Bảo Đại, nh&agrave; d&agrave;i của người M&#39;N&ocirc;ng.</p>\r\n<p>\r\n	12h: Tham quan bu&ocirc;n Jun.</p>\r\n<p>\r\n	13h: Gh&eacute; th&aacute;p Chăm Yang Prong, một trong những th&aacute;p Chăm nổi tiếng của T&acirc;y Nguy&ecirc;n.</p>\r\n<p>\r\n	14h: Hang đ&aacute; Dak Tuar, nơi đ&oacute;ng qu&acirc;n của Tỉnh ủy Đắk Lắk v&agrave; c&aacute;c đơn vị bộ đội chủ lực của tỉnh trong suốt những năm kh&aacute;ng chiến chống Mỹ.</p>\r\n<p>\r\n	15h: Thăm vườn quốc gia Yok Đ&ocirc;n - một trong những khu bảo tồn thi&ecirc;n nhi&ecirc;n lớn nhất nước ta, đ&egrave;o Phượng Ho&agrave;ng - dải n&uacute;i đất được thi&ecirc;n nhi&ecirc;n tạo n&ecirc;n tựa hồ như c&aacute;nh chim phượng tuyệt đẹp.</p>\r\n<p>\r\n	17h: Ăn tối, kh&aacute;m ph&aacute; TP về đ&ecirc;m.</p>\r\n', '2016-03-13 14:49:56', 'images/DUC_8551_DEMK_jpg.jpg', 'Voi thi chạy trong hội đua voi. ', 3, 1),
(12, 4, 1, 'Đắk Mil - Đà Lạt thu nhỏ của Tây Nguyên', 'Đắk Mil là thị xã thuộc tỉnh Đắk Nông, cách TP HCM khoảng 300 km, cách thành phố Buôn Ma Thuột chừng 60 km. Nơi đây có khí hậu trong lành mát mẻ, cảnh sắc thanh bình. ', '<p>\r\n	&oacute; hai c&aacute;ch để c&aacute;c bạn tới Đắk Mil: C&aacute;ch thứ nhất l&agrave; đi bằng xe kh&aacute;ch, thời gian di chuyển cho qu&atilde;ng đường 300 km từ TP HCM chừng 7 giờ kể cả thời gian nghỉ. Gi&aacute; v&eacute; khoảng 170.000-180.000 đồng mỗi kh&aacute;ch, t&ugrave;y h&atilde;ng xe.</p>\r\n<p>\r\n	C&aacute;ch thứ hai l&agrave; đi bằng xe m&aacute;y. Đ&acirc;y cũng l&agrave; c&aacute;ch để c&aacute;c bạn c&oacute; thể chi&ecirc;m ngưỡng được cảnh đẹp tr&ecirc;n đường đi. Hiện nay QL14 đ&atilde; l&agrave;m xong, cảnh hai b&ecirc;n đường rất đẹp. Từ TP HCM, bạn theo QL13 qua địa phận B&igrave;nh Dương, tới ng&atilde; tư Sở Sao ngay khu du lịch Đại Nam th&igrave; rẽ phải, đi theo đường Nguyễn Văn Th&agrave;nh tới thị x&atilde; Đồng Xo&agrave;i. Từ Đồng Xo&agrave;i, bạn theo tiếp QL14 qua c&aacute;c địa danh như B&ugrave; Đăng, Dak R&rsquo;lap, Gia Nghĩa sẽ tới được thị trấn Đắk Mil.</p>\r\n<p>\r\n	Lần&nbsp;đầu đi Đắk Mil, tới gần với thị x&atilde; Gia Nghĩa, m&igrave;nh kh&ocirc;ng đi theo QL14, m&agrave; rẽ theo đường QL14C dọc bi&ecirc;n giới với Campuchia. Đ&acirc;y l&agrave; con đường &iacute;t người qua lại, nh&agrave; cửa thưa thớt nhưng b&ugrave; lại cảnh đẹp tuyệt vời, rất đ&aacute;ng để trải nghiệm.</p>\r\n<p>\r\n	<strong>Ở&nbsp;</strong></p>\r\n<p>\r\n	Đắk Mil kh&ocirc;ng phải l&agrave; một điểm du lịch nổi tiếng, n&ecirc;n kh&ocirc;ng c&oacute; nhiều dịch vụ du lịch. Dọc con đường ch&iacute;nh của thị trấn v&agrave; xung quanh hồ T&acirc;y c&oacute; một v&agrave;i kh&aacute;ch sạn, nh&agrave; nghỉ b&igrave;nh d&acirc;n. Gi&aacute; c&oacute; thể dao động từ 150.00-300.000 đồng.</p>\r\n<p>\r\n	Đắk Mil kh&ocirc;ng th&iacute;ch hợp cho những ai th&iacute;ch sự s&ocirc;i động, n&aacute;o nhiệt cần c&oacute; nhiều chỗ vui chơi, giải tr&iacute;. Đắk Mil th&iacute;ch hợp cho việc trải nghiệm v&agrave; nghỉ ngơi. Khu vực hồ T&acirc;y l&agrave; nơi tập trung nhiều người d&acirc;n đến vui chơi nhất. Hồ rất rộng v&agrave; đẹp, xung quanh c&oacute; những qu&aacute;n c&agrave; ph&ecirc; với tầm nh&igrave;n ra hồ. C&agrave; ph&ecirc; tại đ&acirc;y rất ngon, đậm đặc, kh&aacute;c hẳn đồ uống ở TP HCM.&nbsp;</p>\r\n<p>\r\n	Con đường b&ecirc;n h&ocirc;ng hồ T&acirc;y v&agrave;o khu vực bệnh viện huyện rất đẹp, với những vườn c&agrave; ph&ecirc; hai b&ecirc;n đường. Bạn n&agrave;o th&iacute;ch chụp h&igrave;nh c&oacute; thể đến đ&acirc;y. Nếu chịu kh&oacute; lang thang trong những con đường đất đỏ, l&agrave;ng của người d&acirc;n bản địa, bạn sẽ c&oacute; những bộ ảnh tuyệt đẹp về đời thường, phong cảnh.</p>\r\n', '2016-03-13 14:53:26', 'images/IMG_0996.jpg', 'Đà lạt', 2, 1),
(13, 4, 1, 'Có một mùa hoa đỗ mai đang về ', 'Những ngày này, chạy xe từ Đà Lạt về Sài Gòn, đã thấy những cây hoa đỗ mai trụi lá bên nhiều hiên nhà, vệ đường gần đèo Bảo Lộc lấm tấm đầy nụ. ', '<p>\r\n	Lại nhớ những con đường hoa đỗ mai ở B&agrave; Rịa-Vũng T&agrave;u. Lại nhớ những ghi ch&uacute; trong s&aacute;ch đỗ mai, đỗ đ&agrave;o, đ&agrave;o đậu hay c&ograve;n hay c&ograve;n gọi l&agrave;&nbsp;c&acirc;y cọc r&agrave;o (v&igrave; trồng l&agrave;m h&agrave;ng r&agrave;o), hồng mai, anh đ&agrave;o giả, s&aacute;t thủ&nbsp;đốm (vỏ c&acirc;y ng&acirc;m nước l&agrave;m thuốc diệt chuột).</p>\r\n<p>\r\n	C&acirc;y&nbsp;Gliricidia maculata&nbsp;thuộc&nbsp;họ đậu (Fabaceae) c&oacute; từ rừng tự nhi&ecirc;n ch&acirc;u Mỹ,&nbsp;đến Việt Nam theo con đường thực d&acirc;n của người Ph&aacute;p.&nbsp;Như nhiều lo&agrave;i c&acirc;y kh&aacute;c, cuối đ&ocirc;ng l&agrave; m&ugrave;a của đỗ mai trụi l&aacute; để xu&acirc;n đến trổ hoa v&agrave; l&aacute; non.</p>\r\n<p>\r\n	M&ugrave;a hoa t&ugrave;y theo thời tiết từng v&ugrave;ng m&agrave;&nbsp;k&eacute;o d&agrave;i đến 1 -&nbsp;2 th&aacute;ng. Hoa c&oacute; thể c&oacute; c&aacute;c m&agrave;u hồng sậm, hồng phớt v&agrave; trắng, ch&ugrave;m thưa hay ch&ugrave;m d&agrave;y.</p>\r\n<p>\r\n	Xa xưa, mỗi khi xu&acirc;n về,&nbsp;d&acirc;n di cư&nbsp;miền Bắc ở&nbsp;v&ugrave;ng Bảo Lộc lại ra r&agrave;o bụi, cắt đỗ mai&nbsp;cắm c&agrave;nh v&agrave;o lọ b&igrave;nh để chưng thay cho hoa đ&agrave;o. Nhưng c&agrave;nh hoa coi đẹp vậy m&agrave; khi rời c&acirc;y lại rất mau t&agrave;n n&ecirc;n kh&ocirc;ng được ưa th&iacute;ch nữa.&nbsp;Rồi c&oacute; tin&nbsp;đ&acirc;y l&agrave; lo&agrave;i hoa độc&nbsp;n&ecirc;n c&oacute; l&uacute;c đ&atilde; bị chặt, đốt bỏ.</p>\r\n<p>\r\n	Nhiều năm sau đ&oacute;, đỗ mai &acirc;m thầm từ v&ugrave;ng n&uacute;i T&acirc;y nguy&ecirc;n (Gia Lai, Kom Tum, Đắk Lắk)&nbsp;về xu&ocirc;i. Ban đầu ngự tr&ecirc;n n&uacute;i cao (nơi c&oacute; kh&iacute; hậu lạnh) sau đ&oacute; len xuống c&aacute;c triền n&uacute;i ven biển. Rồi trở th&agrave;nh đặc sản hoa xu&acirc;n ở B&agrave; Rịa-Vũng T&agrave;u,&nbsp;Phước Hải, Long Hải, Cần Giờ...</p>\r\n', '2016-03-13 14:54:39', 'images/domaichaodonmuaxuantrennuivungtaudomaichaodonmuaxuan145465439domaichaodonmuaxuantrennuivungtau.jpg', 'Đỗ mai chào đón mùa xuân trên núi Vũng Tàu', 7, 1),
(14, 1, 1, 'TOP 35 địa điểm du lịch An Giang đẹp hút du khách nhất 2021  ', 'An Giang là một mảnh đất thuộc vùng đồng bằng Sông Cửu Long, nơi đây sở hữu diện tích khá rộng lớn ở miền Tây Nam Bộ. Có cảnh quan thiên nhiên tươi đẹp, có những con sông dài mênh mông, có những ngọn núi cao hùng vĩ, có những khu rừng phủ một màu xanh bát ngát.', '<p>Nh&agrave; mồ Ba Ch&uacute;c</p>\r\n\r\n<p>Nh&agrave; mồ Ba Ch&uacute;c l&agrave; di t&iacute;ch lịch sử được ch&iacute;nh phủ c&ocirc;ng nhận v&agrave;o năm 1980. Nơi đ&acirc;y c&ograve;n lưu giữ hơn 1000 bộ h&agrave;i cốt của những người d&acirc;n v&ocirc; tội. N&ecirc;n ch&iacute;nh v&igrave; vậy được gọi l&agrave; nh&agrave; mồ. V&agrave; được xem l&agrave; một bản c&aacute;o trạng về tội &aacute;c t&agrave;y trời của bọn P&ocirc;n Pốt. Qua đ&oacute; n&ecirc;u l&ecirc;n được gi&aacute; trị nh&acirc;n văn s&acirc;u sắc về t&iacute;nh ch&iacute;nh nghĩa v&agrave; nghĩa vụ cao cả của qu&acirc;n d&acirc;n Việt Nam đối với sự nghiệp chiến đấu v&agrave; giải ph&oacute;ng.</p>\r\n\r\n<h3>Miếu b&agrave; ch&uacute;a Xứ &ndash;Địa điểm du lịch An Giang gắn liền với những truyền thuyết</h3>\r\n\r\n<p>Khi đến An Giang m&agrave; kh&ocirc;ng đến Miếu b&agrave; ch&uacute;a Xứ nơi đ&acirc;y th&igrave; quả l&agrave; một sự thiếu s&oacute;t lớn. Đ&acirc;y l&agrave; một điểm du lịch t&acirc;m linh nổi tiếng kh&ocirc;ng chỉ ở miền T&acirc;y Nam Bộ. M&agrave; ngay cả người Việt Nam ở nước ngo&agrave;i vẫn biết đến miếu n&agrave;y. Miếu b&agrave; ch&uacute;a Xứ nằm ở ch&acirc;n n&uacute;i Sam, phường N&uacute;i Sam, th&agrave;nh phố Ch&acirc;u Đốc, tỉnh An Giang. Miếu c&aacute;ch trung t&acirc;m Ch&acirc;u Đốc khoảng 10km. Nơi đ&acirc;y c&oacute; rất nhiều truyền thuyết huyền b&iacute; được truyền lại từ thế hệ n&agrave;y sang thế hệ kh&aacute;c.</p>\r\n\r\n<div class=\"alignnone wp-caption\" id=\"attachment_21365\" style=\"width:1034px\"><img alt=\"Miếu bà chúa Xứ\" class=\"size-large wp-image-21365\" src=\"https://diachiamthuc.vn/wp-content/uploads/2021/09/Mieu-ba-chua-Xu-1024x576.jpg\" style=\"height:576px; width:1024px\" />\r\n<p>Miếu b&agrave; ch&uacute;a Xứ</p>\r\n</div>\r\n\r\n<h4>Truyền thuyết về Miếu b&agrave; ch&uacute;a Xứ</h4>\r\n\r\n<p>B&agrave; ch&uacute;a Xứ l&agrave; ai? Theo truyền thuyết kể lại, c&aacute;ch đ&acirc;y khoảng v&agrave;i trăm năm th&igrave; người d&acirc;n địa phương đ&atilde; ph&aacute;t hiện ra tượng B&agrave; ở tr&ecirc;n đỉnh n&uacute;i Sam. Tuy nhi&ecirc;n điều huyền b&iacute; kh&aacute;c lạ l&agrave; ch&iacute;nh h&agrave;ng chục thanh ni&ecirc;n đ&ocirc; con khỏe khoắn nhưng vẫn kh&ocirc;ng nhấc được tượng B&agrave; l&ecirc;n. Sau đ&oacute; c&oacute; một b&agrave; c&ocirc; bảo chỉ cần ch&iacute;n người con g&aacute;i th&igrave; c&oacute; thể thỉnh b&agrave; xuống nhưng chỉ xuống được đến ch&acirc;n n&uacute;i. C&oacute; lẽ b&agrave; đ&atilde; chọn nơi đ&acirc;y để an vị v&agrave; người d&acirc;n đ&atilde; lập miếu thờ. Ngo&agrave;i ra miếu b&agrave; ch&uacute;a Xứ cũng gắn với những c&acirc;u chuyện v&ocirc; c&ugrave;ng k&igrave; b&iacute;. Chẳng hạn như truyền thuyết về c&ocirc;ng lao &ocirc;ng Thoại Ngọc Hầu.</p>\r\n\r\n<div class=\"alignnone wp-caption\" id=\"attachment_21364\" style=\"width:1034px\"><img alt=\"Miếu bà chúa Xứ Núi Sam\" class=\"size-large wp-image-21364\" src=\"https://diachiamthuc.vn/wp-content/uploads/2021/09/Mieu-ba-chua-Xu-Nui-Sam-1024x683.jpg\" style=\"height:683px; width:1024px\" />\r\n<p>Miếu b&agrave; ch&uacute;a Xứ N&uacute;i Sam</p>\r\n</div>\r\n\r\n<p>Theo lời kể, &ocirc;ng đi đ&aacute;nh giặc ngoại x&acirc;m th&igrave; vợ &ocirc;ng đ&atilde; khấn v&aacute;i xin b&agrave; Xứ ph&ugrave; hộ để chồng dẹp tan được qu&acirc;n x&acirc;m lược. Quả thật như vậy, &ocirc;ng đ&atilde; đ&aacute;nh tan qu&acirc;n x&acirc;m lược, vẻ vang trở về gi&uacute;p cho l&agrave;ng x&oacute;m y&ecirc;n b&igrave;nh. Sau đ&oacute; &ocirc;ng cho thỉnh b&agrave; xuống n&uacute;i v&agrave; lập miếu thờ c&uacute;ng. Nhưng trong qu&aacute; tr&igrave;nh x&acirc;y dựng đ&atilde; xảy ra v&ocirc; số chuyện trắc trở, thi&ecirc;n tai, lũ lụt v&agrave; đ&aacute;ng sợ hơn l&agrave; th&uacute; rừng ăn thịt người. Sau v&ocirc; số lần xảy ra chuyện th&igrave; mọi chuyện đ&atilde; su&ocirc;n sẻ hơn nhiều khi miếu b&agrave; Xứ được ho&agrave;n th&agrave;nh.</p>\r\n\r\n<div class=\"alignnone wp-caption\" id=\"attachment_21363\" style=\"width:1010px\"><img alt=\"Miếu bà chúa Xứ An Giang\" class=\"size-full wp-image-21363\" src=\"https://diachiamthuc.vn/wp-content/uploads/2021/09/Mieu-ba-chua-Xu-An-Giang.jpg\" style=\"height:703px; width:1000px\" />\r\n<p>Miếu b&agrave; ch&uacute;a Xứ An Giang</p>\r\n</div>\r\n\r\n<h4>Kiến tr&uacute;c Miếu b&agrave; ch&uacute;a Xứ</h4>\r\n\r\n<p>Đ&oacute; l&agrave; một trong những truyền thuyết hay nhất. Đến đ&acirc;y bạn sẽ được nghe những truyền thuyết kh&aacute;c v&ocirc; c&ugrave;ng t&acirc;m linh, r&ugrave;ng m&igrave;nh. Vậy c&ograve;n miếu thờ b&agrave; được x&acirc;y dựng ra sao? Ban đầu, &ocirc;ng Hầu đ&atilde; x&acirc;y v&ocirc; c&ugrave;ng đơn sơ chỉ bằng gỗ. Nhưng m&atilde;i đến sau n&agrave;y th&igrave; người d&acirc;n đ&atilde; dựng l&ecirc;n một ng&ocirc;i miếu v&ocirc; c&ugrave;ng khang trang. C&oacute; kiến tr&uacute;c v&ocirc; c&ugrave;ng đẹp mắt v&agrave; đậm chất nghệ thuật. Ph&iacute;a tr&ecirc;n cao thở những thần khỏe mạnh giang tay đỡ những đầu k&egrave;o. C&aacute;c c&aacute;nh cửa đều được chạm khắc v&ocirc; c&ugrave;ng tinh tế với những hoa văn tinh xảo, lộng lẫy.</p>\r\n\r\n<div class=\"alignnone wp-caption\" id=\"attachment_21362\" style=\"width:1034px\"><img alt=\"Miếu bà chúa Xứ - Địa điểm du lịch tâm linh\" class=\"size-large wp-image-21362\" src=\"https://diachiamthuc.vn/wp-content/uploads/2021/09/Mieu-ba-chua-Xu-Dia-diem-du-lich-tam-linh-1024x678.jpg\" style=\"height:678px; width:1024px\" />\r\n<p>Miếu b&agrave; ch&uacute;a Xứ &ndash;Địa điểm du lịch t&acirc;m linh</p>\r\n</div>\r\n\r\n<p>Đặc biệt, bốn c&acirc;y cột trước ch&aacute;nh điện vẫn được giữ nguy&ecirc;n như l&uacute;c đầu. Miếu c&oacute; h&igrave;nh chữ &ldquo;Quốc&rdquo;, h&igrave;nh dạng hoa sen đang nở. C&aacute;c hoa văn được chạm trổ theo hướng của c&aacute;c nước T&acirc;y &Aacute;. Ở ch&aacute;nh điện, b&agrave; ch&uacute;a Xứ được đặt tr&ecirc;n bệ cao v&agrave; s&aacute;t hai b&ecirc;n l&agrave; hai con hạc trắng biểu trưng cho đ&ocirc;i c&aacute;nh của b&agrave;. B&ecirc;n phải b&agrave; l&agrave; một linga, được gọi l&agrave; b&agrave;n thờ Cậu. B&ecirc;n tr&aacute;i b&agrave; l&agrave; h&igrave;nh Yoni bằng gỗ, được gọi l&agrave; b&agrave;n thờ C&ocirc;. H&agrave;ng năm, c&oacute; rất nhiều lễ hội để nh&acirc;n d&acirc;n v&agrave; kh&aacute;ch ở mọi nơi đến cầu nguyện, xin được ban phước. Chẳng hạn như lễ Tắm B&agrave;, lễ Thỉnh Sắc, lễ T&uacute;c Yết, lễ Ch&aacute;nh Tế, lễ Hồi Sắc,&hellip;</p>\r\n\r\n<ul>\r\n	<li>Địa chỉ: dưới ch&acirc;n&nbsp;n&uacute;i Sam&nbsp;thuộc phường&nbsp;n&uacute;i Sam, TP Ch&acirc;u Đốc, tỉnh An Giang</li>\r\n	<li>Giờ mở cửa: Cả ng&agrave;y</li>\r\n	<li>Gi&aacute; v&eacute;: Miễn ph&iacute;</li>\r\n	<li>Đ&aacute;nh gi&aacute;: 4,5/5</li>\r\n</ul>\r\n', '2021-12-02 21:55:20', 'images/dlag.png', '', 21, 1);
INSERT INTO `tbl_noidungdiadiem` (`MaBaiViet`, `MaChuDe`, `MaNguoiDung`, `TieuDe`, `TomTat`, `NoiDung`, `NgayDang`, `HinhAnh`, `ChuThichAnh`, `LuotXem`, `KiemDuyet`) VALUES
(15, 4, 1, 'Người Nhật dọn vườn, trồng anh đào ở Paris để thu hút khách', 'Các công ty du lịch của Nhật sẽ thực hiện dự án sửa sang vườn Trocadero cạnh tháp Eiffel, và trồng anh đào ở các khu đông khách để kéo thêm nhiều khách Nhật Bản sang Pháp. ', '<p>\r\n	Dự án này do Hiệp hội du lịch Paris thực hiện. Đây là một nhóm công ty du lịch và hãng hàng không Nhật Bản. Họ hy vọng tạo được môi trường sạch sẽ, ngăn nắp hơn ở thủ đô của Pháp để hút du khách Nhật tới đây.</p>\r\n<p>\r\n	Từ ngày 13/3, hiệp hội sẽ bắt đầu chỉnh trang vườn Trocadero cạnh tháp Eiffel, và trồng thêm cây anh đào ở các điểm du lịch đông khách</p>\r\n<p>\r\n	Hàng ngày có khoảng 5.000 người dọn dẹp Kinh đô Ánh sáng, nhưng rác rưởi, đầu lọc thuốc lá và phân thú cưng vẫn xuất hiện trên các tuyến phố, khiến người Nhật không thoải mái.</p>\r\n<p>\r\n	Đây không phải lần đầu người Nhật ra tay để khiến Paris hấp dân hơn. Từ năm 2007, một đội từ tổ chức môi trường Green Bird đã thường xuyên chỉnh trang các điểm du lịch hút khách ở Pháp, trong đó có Champs Élysées và tháp Eiffel.</p>\r\n<p>\r\n	Với ngành du lịch đang cố phục hồi sau vụ tấn công khủng bố, thị trưởng thành phố Paris, bà Anne Hidalgo, đã tới Tokyo tháng trước nhằm khuyến khích nhiều du khách Nhật Bản tới Pháp hơn.</p>\r\n<p>\r\n	Mỗi năm Paris đón khoảng 600.000 khách Nhật, một trong 5 thị trường khách du lịch lớn nhất của thành phố này.</p>\r\n<p>\r\n	Không chỉ Hiệp hội du lịch Paris nỗ lực cải thiện hình ảnh của Kinh đô Ánh sáng, gần đây Hội đồng thành phố cũng đã công bố kế hoạch trị giá 28 triệu USD để biến Paris thành “hình mẫu của sự sạch sẽ”. Dự án này sẽ giúp tặng lượng lao công và hiện đại hóa trang thiết bị cho họ. Một ứng dụng sẽ được công bố, cho phép cư dân báo tình trạng vệ sinh đường phố cho lao công để họ nhanh chóng xử lý.</p>\r\n', '2016-03-13 15:00:10', 'images/32191C7F000005780imagea1_1457709870412.jpg', 'Vườn Trocadero cạnh tháp Eiffel là điểm đến đông khách tham quan ở Paris. Ảnh: Daily Mail.', 1, 1);

